mapper.select:
SELECT CASE WHEN COUNT(*) > 0 THEN 1 ELSE 0 END
FROM reports r
JOIN report_details d ON r.report_detail_idx = d.report_detail_idx
WHERE r.reporting_user_idx = #{reporterId}
AND d.entity_type = #{entityType}
AND d.entity_idx = #{entityId}

SELECT report_detail_idx
FROM report_details
WHERE entity_type = #{entityType}
AND entity_idx = #{entityId}
ORDER BY report_detail_idx DESC
FETCH FIRST 1 ROWS ONLY
