mapper.insert:
INSERT INTO users (
user_id, provider, user_name, nickname, birth_date, gender, user_ci, password_hash,
email, phone, profile_name, introduction, last_login_at, update_at, ghost
) VALUES (
#{userId}, #{provider}, #{userName}, #{nickname}, #{birthDate, jdbcType=DATE}, #{gender, jdbcType=CHAR}, #{userCi, jdbcType=VARCHAR}, #{passwordHash},
#{email}, #{phone, jdbcType=VARCHAR}, #{profileName}, #{introduction, jdbcType=VARCHAR}, #{lastLoginAt, jdbcType=DATE}, #{updateAt, jdbcType=DATE}, #{ghost, jdbcType=DATE}
)

mapper.select:
SELECT *
FROM users
WHERE provider = #{provider}
AND user_id = #{userId}
AND user_status != 'DELETED'

